srcUsr = load "${App.EdgeMarts.user.Alias}";
usr_filtered = filter srcUsr by 'ParentRole.DeveloperName' in {{ selection(filter_role) }};

srcOppty = load "${App.EdgeMarts.opportunity.Alias}";
srcOppty = filter srcOppty by 'IsClosed'== "true" ;
srcOppty = filter srcOppty by 'IsWon' == "true"  ;
srcOppty = filter srcOppty by '${Variables.Bookings.fieldName}' in {{ selection(Type_6) }}  ;
srcOppty = filter srcOppty by date('CloseDate_Year', 'CloseDate_Month', 'CloseDate_Day') in [{{ no_quote(value(field(selection(step_grouplist3), 'Current_pigql'))) }}] ;

reps = filter srcOppty by 'Owner.Role.DeveloperName' in {{ selection(filter_role) }};
reps = group reps by 'OwnerId';
reps = foreach reps generate 'OwnerId', first('Owner.Name') as 'Group By', coalesce((sum('CloseDate_day_epoch') - sum('CreatedDate_day_epoch'))/count(),0)  as 'avg_Days_Open';
d1 = group usr_filtered by 'Id';
d1 = foreach d1 generate 'Id';
d2 = group srcUsr by 'Id';
d2 = foreach d2 generate 'Id';
d3 = union d1, d2;
z1 = group reps by 'OwnerId' left, d3 by 'Id';
z1 = foreach z1 generate first(reps['Group By']) as 'Owner.Name', round(sum(reps['avg_Days_Open'])) as 'avg_Days_Open';

own = filter srcOppty by 'Owner.Role.DeveloperName' in {{ results(mgr_get_manager,'Role.DeveloperName') }} or 'Owner.Role.DeveloperName' in {{ selection(filter_role) }}  ;
own = group own by 'Owner.Role.DeveloperName'  ;
own = foreach own generate "S" as 'GroupType','Owner.Role.DeveloperName' as 'Group By', sum('CloseDate_day_epoch') - sum('CreatedDate_day_epoch') as 'Days_Open' ,count() as 'count'  ;
dir = filter srcOppty by 'Owner.RoleNames' in {{ results(mgr_get_manager,'Role.DeveloperName') }}  ;
dir = group dir by 'Owner.RoleNames'  ;
dir = foreach dir generate "D" as 'GroupType','Owner.RoleNames' as 'Group By', sum('CloseDate_day_epoch') - sum('CreatedDate_day_epoch') as 'Days_Open' ,count() as 'count'  ;
oppty = union own,dir;
oppty = group oppty by 'Group By';
oppty = foreach oppty generate 'Group By' as 'Group By',  coalesce(sum('Days_Open')/sum('count') ,0) as 'avg_Days_Open' ;
d4 = group usr_filtered by 'Role.DeveloperName';
d4 = foreach d4 generate 'Role.DeveloperName';
z2 = group oppty by 'Group By', d4 by 'Role.DeveloperName';
z2 = foreach z2 generate oppty['Group By'] as 'Owner.Name', round(sum(oppty['avg_Days_Open'])) as 'avg_Days_Open';
z3 = union z1, z2;

